  def lengthOfLongestSubstring(s: String): Int = {
    var char_map: Map[Char, Int] = Map.empty
    var max: Int = if (s.isEmpty) 0 else 1

    s.zipWithIndex foreach { case (c, idx) =>
      char_map.get(c) foreach { n =>
        max = char_map.size.max(max)
        char_map = char_map.filter(_._2 > n)
      }
      char_map += c -> idx
    }
    max
  }

  def lengthOfLongestSubstring2(s: String): Int = {
    val len: Int = s.length
    var ans: Int = if (s.isEmpty) 0 else 1
    var i: Int = 0
    var j: Int = 0
    val set: mutable.HashSet[Character] = new mutable.HashSet[Character]()
    while (i < len && j < len) {
      if (!set.contains(s(j))) {
        set.add(s(j))
        ans = ans.max(j - i)
        j = j + 1
      } else {
        set.remove(s(i))
        i = i + 1
      }
    }
    ans
  }

  def lengthOfLongestSubstring3(s: String): Int = {
    val char_map: mutable.Map[Char, Int] = mutable.Map.empty
    var ans: Int = 0
    var i: Int = 0

    s.zipWithIndex foreach { case (c, j) =>
      char_map.get(c) foreach { n =>
        i = ans.max(j - i + 1)
      }
      ans = ans.max(j - i + 1)
      char_map += c -> (j + 1)
    }
    ans
  }
